$(document).ready(function(){$('.slidertop').bxSlider({slideWidth:150,minSlides:2,maxSlides:6,moveSlides:1,slideMargin:10,pager:false});$('#top_search, #footer-search').keypress(function(e){if(e.which==13){jQuery(this).blur();var keywords=$(this).serialize().toLowerCase();var keys=keywords.split('=');var finalkeys=keys[1].replace(/\+/g,"-");window.location.href="/search/"+finalkeys;event.preventDefault();}});$('#footer-submit').click(function(){var keywords=$('#footer-search').serialize().toLowerCase();var keys=keywords.split('=');var finalkeys=keys[1].replace(/\+/g,"-");window.location.href="/search/"+finalkeys;event.preventDefault();});$('#main-search-go').submit(function(event){$('#main-search').blur();var keywords=$('#main-search').val();var keywords=keywords.replace(/ /gi,"-");keywords=encodeURIComponent(keywords);window.location.href="/search/"+keywords;event.preventDefault();});$('a[href*="[at]"][href*="[dot]"]').each(function(){var email=$(this).attr('href').split('[at]').join('@').split('[dot]').join('.');$(this).attr('href','mailto:'+email.toLowerCase());if($(this).text().length==0)$(this).text(email);});$('.left_list span').click(function(e){var div_id=$(this).parents('div:eq(1)').attr('id');$('#'+div_id+' span.clkd').removeClass('clkd');$(this).addClass('clkd');var textoutput=$(this).text();textoutput=textoutput.toLowerCase().replace(" ","-");if(div_id=='top_films_left'){var lowerdiv='top_films_area';}if(div_id=='coming_soon_left'){var lowerdiv='coming_soon_area';}if(div_id=='out_now_left'){var lowerdiv='out_now_area';}topswap(lowerdiv,textoutput);return false;});});$('.age-gate form .submit').click(function(){event.preventDefault();$('.age-gate').fadeOut();})
$('div.mobile-nav').click(function(){$(this).toggleClass('clicked');$('#top_nav').slideToggle();});function rerunslide(){$('.slidertop').bxSlider({slideWidth:150,minSlides:2,maxSlides:6,moveSlides:1,slideMargin:10,pager:false});}var acounter=0;function topswap(id,genre){if(id=='top_films_area'){var filecat='topfilms';}if(id=='coming_soon_area'){var filecat='comingsoon';}if(id=='out_now_area'){var filecat='outnow';}$.get('/ajax/'+filecat+'-'+genre+'.txt',function(response){var newresponse='<div class="slidertop">'+response+'</div>';$('#'+id+'').fadeTo("fast",0.01,function(){$('#'+id+'').empty();$('#'+id+'').append(newresponse);rerunslide();setTimeout(function(){$('#'+id+'').fadeTo("fast",1);},300);});});acounter++;if(acounter%3==0){refreshBids();}}